mapper.select:

SELECT a.*, (SELECT p.pname FROM protection p WHERE p.pid = a.pid) AS pname
FROM adoption AS a
WHERE a.oid = #{oid}
ORDER BY a.aid DESC;


SELECT a.*, (SELECT p.pname FROM protection p WHERE p.pid = a.pid) AS pname
FROM adoption AS a
WHERE a.nid = #{nid}
ORDER BY a.aid DESC;

mapper.update:

 			UPDATE adoption  
 			SET acondition = #{acondition} 
			WHERE aid = #{aid};
