mapper.update:

UPDATE freeboard
SET
btitle = #{btitle},
bcontent = #{bcontent}
WHERE bid = #{bid};

mapper.select:

SELECT fr.*, m.mname as rwriterName
FROM freeBoardReply fr
LEFT JOIN member m ON (fr.rwriter = m.username)
WHERE bid = #{bid}
ORDER BY rid DESC;

mapper.update:

UPDATE freeBoardReply
SET
rcontent = #{rcontent}
WHERE rid = #{rid};
