mapper.select:

SELECT bid, btitle, bcontent, bwriter, date_format(bdate, '%Y-%m-%d') as bdate, bhit
FROM freeboard
WHERE bid=#{bid};

mapper.update:

UPDATE freeboard
SET
btitle = #{btitle},
bcontent = #{bcontent}
WHERE bid = #{bid};

mapper.select:

SELECT *
FROM freeBoardReply
WHERE bid = #{bid}
ORDER BY rid DESC;

mapper.update:

UPDATE freeBoardReply
SET
rcontent = #{rcontent}
WHERE rid = #{rid};
